    public static void processErrorResponse(VolleyError error, Activity context) {

        NetworkResponse networkResponse = error.networkResponse;

        if (networkResponse != null) {
            switch (networkResponse.statusCode) {
                case Constants.CODE_UNAUHTHORIZED:
                    Util.showMessage(context, Constants.MESSAGE_UNAUHTHORIZED);
                   break;
                case Constants.CODE_BADREQUEST:
                    if (networkResponse.data != null) {
                        try{
                            String jsonError = new String(networkResponse.data);
                            JSONObject obj = new JSONObject(jsonError);
                            String message = obj.getString("message");
                            showMessage(context, message);
                        } catch(JSONException e){
                            Util.showMessage(context, Constants.MESSAGE_BADREQUEST);
                        }
                    } else {
                        Util.showMessage(context, Constants.MESSAGE_BADREQUEST);
                    }
                    break;
                case Constants.CODE_FORBIDDEN:
                    Util.showMessage(context, Constants.MESSAGE_FORBIDDEN);
                    break;
                case Constants.CODE_NOTFOUND:
                    Util.showMessage(context, Constants.MESSAGE_NOTFOUND);
                    break;
                case Constants.CODE_SERVERERROR:
                    Util.showMessage(context, Constants.MESSAGE_SERVERERROR);
                    break;
                default:
                    Util.showMessage(context, Constants.ERROR_REQUEST);
                    break;
            }

        }
    }

    public static void processErrorResponse(int statusCode, Activity context) {

        if (statusCode != 0) {
            switch (statusCode) {
                case Constants.CODE_UNAUHTHORIZED:
                    Util.showMessage(context, Constants.MESSAGE_UNAUHTHORIZED);
                    break;
                case Constants.CODE_BADREQUEST:
                    Util.showMessage(context, Constants.MESSAGE_BADREQUEST);
                    break;
                case Constants.CODE_FORBIDDEN:
                    Util.showMessage(context, Constants.MESSAGE_FORBIDDEN);
                    break;
                case Constants.CODE_NOTFOUND:
                    Util.showMessage(context, Constants.MESSAGE_NOTFOUND);
                    break;
                default:
                    Util.showMessage(context, Constants.ERROR_REQUEST);
                    break;
            }

        }

    }


    public static void saveUserSession(Context context, User user) {
        SharedPreferences settings = context.getSharedPreferences(PREFS_NAME, 0);
        SharedPreferences.Editor editor = settings.edit();
        Gson gson = new Gson();
        String json = gson.toJson(user);
        editor.putString(USER_INFO, json);
        editor.apply();
    }


    public static boolean isConnectedToInternet(Context context) {
        int[] networkTypes = {ConnectivityManager.TYPE_MOBILE,
                ConnectivityManager.TYPE_WIFI};
        try {
            ConnectivityManager connectivityManager =
                    (ConnectivityManager) context.getSystemService(Context.CONNECTIVITY_SERVICE);
            for (int networkType : networkTypes) {
                assert connectivityManager != null;
                NetworkInfo activeNetworkInfo = connectivityManager.getActiveNetworkInfo();
                if (activeNetworkInfo != null &&
                        activeNetworkInfo.getType() == networkType)
                    return true;
            }
        } catch (Exception e) {
            return false;
        }
        Toast.makeText(context, context.getResources().getString(R.string.no_internet_connection), Toast.LENGTH_LONG).show();
        return false;
    }




